mapper.select:
SELECT DISTINCT s.name AS brand, p.prod_code AS prod_code, p.name AS prod_name, p.price AS prod_price, p.sale AS prod_sale, p.prod_image AS prod_image, p.saled_price AS prod_saled_price, i.option_name AS option_name, o.* FROM `order` o INNER JOIN product p ON o.prod_no = p.id INNER JOIN seller s ON p.seller_no = s.id LEFT JOIN inventory i ON i.id = o.inventory_no WHERE o.cus_no = #{cus_no}
AND o.order_date BETWEEN #{startDate} AND #{endDate}
ORDER BY o.order_date DESC

SELECT SUM(p.price) FROM `order` o
INNER JOIN product p ON o.prod_no = p.id
WHERE order_code = #{order_code} AND cus_no = #{cus_no}

mapper.update:
UPDATE `order`
SET deli_no = #{deli_no}
WHERE order_code = #{order_code}
AND cus_no = #{cus_no}

UPDATE `order`
SET status = 7, refund_bank = #{refund_bank}, refund_account = #{refund_account},
reason = #{reason}, retrieve_deli_no = #{retrieve_deli_no}
WHERE cus_no = #{cus_no} AND order_code = #{order_code}
AND prod_no = #{prod_no}

UPDATE `order`
SET status = 8, reason = #{reason}, exchange_option = #{exchange_option}, retrieve_deli_no = #{retrieve_deli_no}
WHERE cus_no = #{cus_no} AND order_code = #{order_code}
AND prod_no = #{prod_no}
