mapper.update:
UPDATE product
SET is_del = 0
WHERE prod_no  = #{id}

mapper.select:
SELECT * FROM product
WHERE seller_no = #{id}
AND is_del = 0;

SELECT * FROM product
WHERE seller_no=#{seller_no}
AND name LIKE CONCAT('%',#{prod_name},'%'}
AND is_del = 0;

SELECT * FROM product
WHERE seller_no=#{seller_no}
AND category_no = #{category}
AND name LIKE CONCAT('%',#{prod_name},'%'}
AND is_del = 0;
