mapper.select:
SELECT b.*, c.name AS c_name, c.gender AS c_gender, c.height AS c_height, c.weight AS c_weight, MAX(o.inventory_no) AS inventory_no, MAX(i.option_name) AS i_option_name FROM board b LEFT JOIN customer c ON b.cus_no = c.id AND c.is_del = 0 LEFT JOIN (SELECT inventory_no, cus_no, prod_no FROM shop.order WHERE prod_no = #{prod_no}) o ON b.cus_no = o.cus_no AND b.prod_no = o.prod_no LEFT JOIN inventory i ON o.inventory_no = i.id WHERE b.bname = 'review' AND b.is_del = 0 AND b.prod_no = #{prod_no}
AND c.gender = #{gender}
AND c.height = #{height}
AND c.weight = #{weight}
GROUP BY b.id, c.name, c.gender, c.height, c.weight
